import React, { useState } from 'react'
import { BsThreeDots } from 'react-icons/bs'
import { MdPlaylistAddCheck } from 'react-icons/md'
import { RiPlayListAddFill, RiShareForwardLine, RiThumbUpFill, RiThumbUpLine } from 'react-icons/ri'
import {AiFillDislike, AiOutlineDislike} from 'react-icons/ai'

import './BtnsVideoPage.css'

function LikeWatchLaterSaveBtns() {
    const [saveVideo, setSaveVideo] = useState(false)
    const [likeVideo, setLikeVideo] = useState(false)
    const [dislikeVideo, setdisLikeVideo] = useState(false)
    function toggleSave()
    {
        if(saveVideo)
        {
            setSaveVideo(false)
        }
        else
        {
            setSaveVideo(true)
        }
    }
    function toggleLikeDislike()
    {
        if(likeVideo===false && dislikeVideo===false)
        {
            setLikeVideo(false)
            setdisLikeVideo(false)
        }
        else
        {
            setLikeVideo(true)
            setdisLikeVideo(false)
        }
    }
    return (
        <>
            <div className="buttons_videoPage">
                <div className="btn_videoPage">
                    <BsThreeDots size={22} />
                </div>
                <div className="btn_videoPage">
                    <div className="like_save_videoPage" onClick={toggleSave}>
                        {
                            saveVideo ? (
                                <>
                                    <MdPlaylistAddCheck className='like_save_video' size={22} />
                                    <b>Saved</b>
                                </>
                            ) :
                            (
                                <>
                                    <RiPlayListAddFill className='like_save_video' size={22} />
                                    <b>Save</b>
                                </>
                            )
                        }
                    </div>
                    <div className="like_save_videoPage">
                        <RiShareForwardLine className='like_save_video' size={22} />
                        <b>Share</b>
                    </div>
                    <div className="like_save_videoPage" onClick={toggleLikeDislike}>
                        {
                            dislikeVideo ? (
                                <>
                                    <AiFillDislike className='like_save_video' size={22} />
                                </>
                            ) :
                            (
                                <>
                                    <AiOutlineDislike className='like_save_video' size={22} />
                                </>
                            )
                        }
                    </div>
                    <div className="like_save_videoPage" onClick={toggleLikeDislike}>
                        {
                            likeVideo ? (
                                <>
                                    <RiThumbUpFill className='like_save_video' size={22} />
                                </>
                            ) :
                            (
                                <>
                                    <RiThumbUpLine className='like_save_video' size={22} />
                                </>
                            )
                        }
                        <b>135K</b>
                    </div>
                </div>
            </div>
        </>
    )
}

export default LikeWatchLaterSaveBtns
