import React from 'react'
import {WiCelsius} from 'react-icons/wi'
import './LocTemp.css'

const LocTemp = ({setlocbtn}) => {
    function getLocation() {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(ShowPos)
        }
        else {
            alert("GeoLocation Not Supported in your Device")
        }
    }
    function ShowPos(position) {
        lat = position.coords.latitude
        lon = position.coords.longitude
        fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&units=metric&appid=c11ef6bb5b19aebc55d38c97e77a3a55`)
            .then(res => res.json())
            .then(displayData)
            .catch(err => alert("City Not Found"))
    }
    return (
        <div className="Loc_container" onClick={() => setlocbtn(false)}>
            <div className="Loc_container2">
                <div className="Temperature">
                    19 <WiCelsius style={{scale: "1",position: "relative", bottom: "5px",right: "14px"}}/>
                </div>
                <div className="City_Details">
                    Bhubaneswar
                </div>
                <div className="weather_description">
                    Mild Rain
                </div>
            </div>
        </div>
    )
}

export default LocTemp
