const currentuserreducer=(state,action)=>{
    switch (action.type) {
        case "FETCH_CURRENT_USER":
            return action.payload
        case 'POST_POINTS':
            return state._id === action.payload._id? action.payload:state
        default:
            return state;
    }
}
export default currentuserreducer;
