const currentuserreducer=(state=null,action)=>{
    switch (action.type) {
        case "FETCH_CURRENT_USER":
            return action.payload
        case 'POST_POINTS':
            return action.payload
        default:
            return state;
    }
}
export default currentuserreducer;
