import React, { useState } from 'react'
import './Comments.css'
import DisplayComments from './DisplayComments'

function Comments() {
    const [commentText, setCommentText] = useState("")
    const commentList = [
        {
            commentBody: "hello",
            userCommented: "ABC",
        },
        {
            commentBody: "Hii",
            userCommented: "XYZ",
        },
        {
            commentBody: "world",
            userCommented: "123",
        },
    ]
    function handleOnChange(event) {
        setCommentText(event.target.value)
    }
    function handleOnSubmit(event) {
        event.preventDefault()
    }
    return (
        <>
            <form action="" className='comments_sub_form_comments' onSubmit={handleOnSubmit}>
                <input type="text" value={commentText} onChange={handleOnChange} placeholder='add comment' className='comment_ibox' />
                <input type="submit" value="add" className='comment_add_btn_comments' />
            </form>
            <div className="display_comments_container">
                {
                    commentList?.map(m => {
                        return (
                            <DisplayComments commentBody={commentBody} userCommented={userCommented} />
                        )
                    })
                }
            </div>
        </>
    )
}

export default Comments
