import React, { useState, useContext } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { SocketContext } from '../../Context';
import './Sidebar.css'

const Sidebar = ({ children }) => {
  const { me, callAccepted, name, setName, callEnded, leaveCall, callUser } = useContext(SocketContext);
  const [idToCall, setIdToCall] = useState('');
  console.log(me)

  return (
    <div className="container">
      <div className="paper1">
        <form className="root" autoComplete="off">
          <div className="gridContainer">
            <div className="padding">
              <div>Account Info</div>
              <input placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
              <CopyToClipboard text={me} className="margin">
                <div startIcon={<div fontSize="large" />}>
                  Copy Your ID
                </div>
              </CopyToClipboard>
            </div>
            <div className="padding">
              <div>Make a call</div>
              <input placeholder="ID to call" value={idToCall} onChange={(e) => setIdToCall(e.target.value)} />
              {callAccepted && !callEnded ? (
                <div startIcon={<div fontSize="large" />} onClick={leaveCall} className="margin">
                  Hang Up
                </div>
              ) : (
                <div startIcon={<div fontSize="large" />} onClick={() => callUser(idToCall)} className="margin">
                  Call
                </div>
              )}
            </div>
          </div>
        </form>
        {children}
      </div>
    </div>
  );
};

export default Sidebar;
