import React, { useContext, useEffect } from 'react'
import './screenshare.css'
import { SocketContext } from '../../Context'

const ScreenVideo = () => {
    const { share,screen, myScreenVideo, userScreenVideo, screenAccepted, screenEnded, answerScreen } = useContext(SocketContext)
    useEffect(()=>{
        if(share.isReceivingScreen){
            answerScreen()
        }
    },[])
    return (
        <>
            {
                screen ? (
                    <div>
                        <video muted autoPlay ref={myScreenVideo} className='screenVid' />
                    </div>
                ) : (
                    <></>
                )
            }
            {
                screenAccepted && !screenEnded ? (
                    <div>
                        <video muted autoPlay ref={userScreenVideo} className='screenVid' />
                    </div>
                ) : (
                    <></>
                )
            }
        </>
    )
}

export default ScreenVideo
