import React, { useEffect, useRef, useState } from 'react'
import "./Videopage.css"
import moment from 'moment'
import Likewatchlatersavebtns from './Likewatchlatersavebtns.jsx'
import { useParams, Link } from 'react-router-dom'
import Comment from '../../Component/Comment/Comment.jsx'
// import vidd from "../../Component/Video/vid.mp4"
import { viewvideo } from '../../action/video.js'
import { addtohistory } from '../../action/history.js'
import { useSelector, useDispatch } from 'react-redux'
import { userpoint } from '../../action/channeluser.js'
import { setcurrentuser } from '../../action/currentuser.js';


const Videopage = ({setUsp}) => {
    const { vid } = useParams();
    const dispatch = useDispatch()
    const vids = useSelector((state) => state.videoreducer)
    // const vids = [
    //     {
    //         _id: 1,
    //         video_src: vidd,
    //         chanel: "wvjwenfj3njfwef",
    //         title: "video 1",
    //         uploader: "abc",
    //         description: "description of video 1"
    //     },
    //     {
    //         _id: 1,
    //         video_src: vidd,
    //         chanel: "wvjwenfj3njfwef",
    //         title: "video 1",
    //         uploader: "abc",
    //         description: "description of video 1"
    //     },
    //     {
    //         _id: 2,
    //         video_src: vidd,
    //         chanel: "wvjwenfj3njfwef",
    //         title: "video 2",
    //         uploader: "abc",
    //         description: "description of video 2"
    //     },
    //     {
    //         _id: 3,
    //         video_src: vidd,
    //         chanel: "wvjwenfj3njfwef",
    //         title: "video 3",
    //         uploader: "abc",
    //         description: "description of video 3"
    //     },
    //     {
    //         _id: 4,
    //         video_src: vidd,
    //         chanel: "wvjwenfj3njfwef",
    //         title: "video 4",
    //         uploader: "abc",
    //         description: "description of video 4"
    //     },
    // ]
    // console.log( vids)
    const vv = vids?.data?.filter((q) => q._id === vid)[0]

    const currentuser = useSelector(state => state.currentuserreducer)

    const handleviews = () => {
        dispatch(viewvideo({ id: vid }))
    }
    const handlehistory = () => {
        dispatch(addtohistory({
            videoid: vid,
            viewer: currentuser?.result._id,
        }))
    }
    const updatePoints = async () => {
        alert("Thanks")
        setUsp(true)
        dispatch(userpoint({ id: currentuser?.result._id }))
    }
    useEffect(() => {
        if (currentuser) {
            handlehistory()
        }
        handleviews()
    }, [])
    useEffect(() => {
        const videoControl = document.getElementById('myvideo')
        videoControl.addEventListener("ended", updatePoints)
    }, [currentuser?.result.points, dispatch])


    // Custom Video Contols
    const videoWidth = useRef(null)
    useEffect(()=>{
        if(document.fullscreenElement === document.getElementById('myvideo'))
        {
            videoWidth.current = document.getElementById('myvideo').clientWidth
            document.getElementById('myvideo').addEventListener("mousedown", playbackStart)
            document.getElementById('myvideo').addEventListener("mouseup", playbackStop)
        }
        else
        {
            videoWidth.current = document.getElementById('myvideo').clientWidth
            document.getElementById('user_control').addEventListener("mousedown", playbackStart)
            document.getElementById('user_control').addEventListener("mouseup", playbackStop)   
        }
    },[])
    
    // document.getElementById('user_control').addEventListener("click", handletaps)
    // document.getElementById('user_control').addEventListener("dblclick", handletwotap)

    // document.getElementById('myvideo').addEventListener("click", handletaps)
    // document.getElementById('myvideo').addEventListener("dblclick", handletwotap)

    function playbackStop() {
        document.getElementById('myvideo').playbackRate = 1
    }
    function playbackStart(event) {
        console.log(videoWidth)
        if (event.clientX > (videoWidth)*0.8) {
            document.getElementById('myvideo').playbackRate = 2
        }
        else if (event.clientX < (videoWidth)*0.4) {
            document.getElementById('myvideo').playbackRate = 0.25
        }
        else
        {
            document.getElementById('myvideo').playbackRate = 1
        }
    }







    return (
        <>
            <div className="container_videoPage">
                <div className="container2_videoPage">
                    <div className="video_display_screen_videoPage">
                        <video src={`http://localhost:5000/${vv?.filepath}`} className="video_ShowVideo_videoPage" id='myvideo' controls>
                        </video>
                        <div className="control_box" id='user_control'></div>
                        <div className="video_details_videoPage">
                            <div className="video_btns_title_VideoPage_cont">
                                <p className="video_title_VideoPage">{vv?.title}</p>
                                <div className="views_date_btns_VideoPage">
                                    <div className="views_videoPage">
                                        {vv?.views} views <div className="dot"></div>{" "}
                                        {moment(vv?.createdat).fromNow()}
                                    </div>
                                    <Likewatchlatersavebtns vv={vv} vid={vid} />
                                </div>
                            </div>
                            <Link to={'/'} className='chanel_details_videoPage'>
                                <b className="chanel_logo_videoPage">
                                    <p>{vv?.uploader.charAt(0).toUpperCase()}</p>
                                </b>
                                <p className="chanel_name_videoPage">{vv?.uploader}</p>
                            </Link>
                            <div className="comments_VideoPage">
                                <h2>
                                    <u>Comments</u>
                                </h2>
                                <Comment videoid={vv?._id} />
                            </div>
                        </div>
                    </div>
                    <div className="moreVideoBar">More videos</div>
                </div>
            </div>
        </>
    )
}

export default Videopage
