import React, { useCallback, useEffect, useState } from 'react'
import ReactPlayer from 'react-player'
import { useSocket } from '../../context/SocketProvider'
import peer from '../../service/peer'

const RoomPage = () => {
    const socket = useSocket()
    const [remoteSocketId, setRemoteSocketId] = useState(null)
    const [myStream, setMyStream] = useState()
    const [remoteStream, setRemoteStream] = useState()

    const handleUserJoined = useCallback(({ email, id }) => {
        console.log(`Email ${email} joined`)
        setRemoteSocketId(id)
    }, [])

    const handleIncomingCall = useCallback(async ({ from, offer }) => {
        setRemoteSocketId(from)
        const stream = await navigator.mediaDevices.getUserMedia({
            audio: true,
            video: true,
        })
        setMyStream(stream)
        console.log("Incoming call", { from, offer })
        const ans = await peer.getAnswer(offer)
        socket.emit('call:accepted', { to: from, ans })
    }, [socket])

    const handleCallAccepted = useCallback(({ from, ans }) => {
        peer.setLocalDescription(ans)
        console.log('Call Accepted!!')
        for (const track of myStream.getTracks()) {
            peer.peer.addTrack(track, myStream);
        }
    }, [myStream])

    const handleNegoNeeded = useCallback(async()=>{
        const offer = await peer.getOffer()
            socket.emit("peer:nego:needed", { offer, to: remoteSocketId })
    },[remoteSocketId,socket])

    const handleNegoNeedIncoming = useCallback(({from,offer})=>{
        const ans = peer.getAnswer(offer)
        socket.emit('peer:nego:done',{ to:from, ans })
    },[socket])

    useEffect(()=>{
        peer.peer.addEventListener('negotiationneeded',handleNegoNeeded)
        return () =>{
            peer.peer.removeEventListener('negotiationneeded',handleNegoNeeded)
        }
    },[handleNegoNeeded])

    useEffect(()=>{
        peer.peer.addEventListener('track',async(ev)=>{
            const str = ev.streams
            setRemoteStream(str)
        })
    },[])

    useEffect(() => {
        socket.on('user:joined', handleUserJoined)
        socket.on('incoming:call', handleIncomingCall)
        socket.on('call:accepted', handleCallAccepted)
        socket.on('peer:nego:needed',handleNegoNeedIncoming)
        return () => {
            socket.off('user:joined', handleUserJoined)
            socket.off('incoming:call', handleIncomingCall)
            socket.off('call:accepted', handleCallAccepted)
            socket.off('peer:nego:needed',handleNegoNeedIncoming)
        }
    }, [socket, handleUserJoined, handleIncomingCall, handleCallAccepted, handleNegoNeedIncoming])

    const handleCallUser = useCallback(async () => {
        const stream = await navigator.mediaDevices.getUserMedia({
            audio: true,
            video: true,
        })
        const offer = await peer.getOffer()
        socket.emit("user:call", { to: remoteSocketId, offer })
        setMyStream(stream)
    }, [remoteSocketId, socket])
    return (
        <div>
            <h1>Room Page</h1>
            <h1>{remoteSocketId ? 'Connected' : "No One In Room"}</h1>
            {
                remoteSocketId && <button onClick={handleCallUser}>Call</button>
            }
            {
                myStream &&
                <>
                    <h1>My Stream</h1>
                    <ReactPlayer
                        playing
                        muted
                        height="226px"
                        width="300px"
                        url={myStream}
                    />
                </>
            }
            {
                remoteStream &&
                <>
                    <h1>Friend's Stream</h1>
                    <ReactPlayer
                        playing
                        muted
                        height="226px"
                        width="300px"
                        url={remoteStream}
                    />
                </>
            }
        </div>
    )
}

export default RoomPage
