import React, { useState } from 'react'
import './Comments.css'

function DisplayComments({ commentBody, userCommented, id }) {
    const [Edit, setEdit] = useState(false)
    const [commentBdy, setCommentBdy] = useState("")
    const handleEdit=(cmtId, cmtBody) => {
        setEdit(true)
        setCommentBdy(cmtBody)
    }
    return (
        <>
            {
                Edit ? (
                    <>
                        <form action="" className='comments_sub_form_comments'>
                            <input type="text" value={commentBdy} placeholder='edit comment' className='comment_ibox' onChange={(e)=> setCommentBdy(e.target.value)} />
                            <input type="submit" value="add" className='comment_add_btn_comments' />
                        </form>
                    </>
                ) : (
                    <>
                        <p className='comment_body'>{commentBody}</p>
                    </>
                )
            }
            <p className="user_commented">- {userCommented} commented</p>
            <p className='EditDel_DisplayComment'>
                <i onClick={()=>handleEdit(id,commentBody)}>Edit</i>
                <i>Delete</i>
            </p>
        </>
    )
}

export default DisplayComments
