import React, { useState } from 'react'
import './SearchBar.css'
import { FaSearch } from 'react-icons/fa'
import { BsMicFill } from 'react-icons/bs'
import SearchList from './SearchList'

function Searchbar() {
    const [list,setlist] = useState("")
    const [searchList,setSearchList] = useState(false)
    const TitleArray = ["video1","video2","Animation video","Movies","News"].filter(q=>q.toUpperCase().includes(list.toUpperCase()))

    function handleOnChange(event)
    {
        setlist(event.target.value)
    }
    return (
        <div className='SearchBar_Container'>
            <div className="search_div">
                <input type="text" className='iBox_SearchBar' placeholder='Search' onChange={handleOnChange} onClick={e=>setSearchList(true)} />
                <FaSearch className='searchIcon_SearchBar' onClick={e=>setSearchList(false)} />
                <BsMicFill className='Mic_SearchBar' />
                {
                    list && searchList && <SearchList TitleArray={TitleArray} />
                }
            </div>
        </div>
    )
}

export default Searchbar
