import React, { useState, useContext } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { SocketContext } from '../../Context';

const Sidebar = ({ children }) => {
  const { me, callAccepted, name, setName, callEnded, leaveCall, callUser } = useContext(SocketContext);
  const [idToCall, setIdToCall] = useState('');
  console.log(me)

  return (
    <div className="container">
      <div elevation={10} className="paper">
        <form className="root" noValidate autoComplete="off">
          <div container className="gridContainer">
            <div item xs={12} md={6} className="padding">
              <div gutterBottom variant="h6">Account Info</div>
              <input label="Name" value={name} onChange={(e) => setName(e.target.value)} fullWidth />
              <CopyToClipboard text={me} className="margin">
                <div variant="contained" color="primary" fullWidth startIcon={<div fontSize="large" />}>
                  Copy Your ID
                </div>
              </CopyToClipboard>
            </div>
            <div item xs={12} md={6} className="padding">
              <div gutterBottom variant="h6">Make a call</div>
              <input label="ID to call" value={idToCall} onChange={(e) => setIdToCall(e.target.value)} fullWidth />
              {callAccepted && !callEnded ? (
                <div variant="contained" color="secondary" startIcon={<div fontSize="large" />} fullWidth onClick={leaveCall} className="margin">
                  Hang Up
                </div>
              ) : (
                <div variant="contained" color="primary" startIcon={<div fontSize="large" />} fullWidth onClick={() => callUser(idToCall)} className="margin">
                  Call
                </div>
              )}
            </div>
          </div>
        </form>
        {children}
      </div>
    </div>
  );
};

export default Sidebar;
