import React, { createContext, useState, useRef, useEffect, useCallback } from 'react';
import { io } from 'socket.io-client';
import Peer from 'simple-peer';

const SocketContext = createContext()

const socket = io('http://localhost:2000');

let videoSender

const ContextProvider = ({ children }) => {
    const [callAccepted, setCallAccepted] = useState(false);
    const [callEnded, setCallEnded] = useState(false);
    const [stream, setStream] = useState();
    const [stream1, setStream1] = useState();
    const [name, setName] = useState('');
    const [call, setCall] = useState({});
    const [me, setMe] = useState('');

    const myVideo = useRef();
    const userVideo = useRef();
    const connectionRef = useRef();

    const [screen, setScreen] = useState()
    const [screenAccepted, setScreenAccepted] = useState(false);
    const [screenEnded, setScreenEnded] = useState(false);
    const myScreenVideo = useRef()
    const userScreenVideo = useRef()

    useEffect(() => {
        navigator.mediaDevices.getUserMedia({ video: true, audio: true })
            .then((currentStream) => {
                setStream(currentStream)
            })
            .catch((er) => alert(er))
        socket.emit('room:join');

        socket.on('callUser', ({ from, name: callerName, signal }) => {
            setCall({ isReceivingCall: true, from, name: callerName, signal });
        });

        return () => {
            socket.off('callUser')
        }

    }, []);

    useEffect(() => {
        socket.on('room:join', (data) => {
            setMe(data)
        })
        return () => {
            socket.off('room:join')
        }
    }, [socket])

    useEffect(() => {
        if (myVideo.current && stream) {
            myVideo.current.srcObject = stream
        }
    }, [stream])
    useEffect(() => {
        if (userVideo.current && stream1) {
            userVideo.current.srcObject = stream1
        }
    }, [stream1])


    const answerCall = () => {
        setCallAccepted(true);
        const peer = new Peer({ initiator: false, trickle: false, stream });
        peer.on('signal', (data) => {
            socket.emit('answerCall', { signal: data, to: call.from });
        });
        peer.on('stream', (currentStream) => {
            videoSender = currentStream
            // userVideo.current.srcObject = currentStream;
            setStream1(currentStream)
        });
        peer.signal(call.signal);
        connectionRef.current = peer;
    };

    const callUser = (id) => {
        const peer = new Peer({ initiator: true, trickle: false, stream });
        peer.on('signal', (data) => {
            socket.emit('callUser', { userToCall: id, signalData: data, from: me, name });
        });
        peer.on('stream', (currentStream) => {
            videoSender = currentStream
            // userVideo.current.srcObject = currentStream;
            setStream1(currentStream)
        });
        socket.on('callAccepted', (signal) => {
            setCallAccepted(true);
            peer.signal(signal);
        });
        connectionRef.current = peer;
    };

    const leaveCall = () => {
        setCallEnded(true);
        connectionRef.current.destroy();
        window.location.reload();
    };


    const startScreenShare = () => {
        navigator.mediaDevices.getDisplayMedia({ video: { mediaSource: 'screen', }, audio: true })
            .then((currstream) => {
                setScreen(currstream)
                let vidTrack = currstream.getVideoTracks()[0]
                if (connectionRef.current) {
                    // How to share
                }
                vidTrack.onended = () => {
                    stopScreenSharing()
                }
            })
            .catch((error) => console.error('Error sharing screen:', error));
    }
    const stopScreenSharing = () => {
        // screen.getTracks().forEach((track) => track.stop())
        setScreenAccepted(false)
        setScreenEnded(true)
        setScreen(null);
    }
    useEffect(() => {
        if (myScreenVideo.current && screen) {
            myScreenVideo.current.srcObject = screen
        }
    }, [screen])



    return (
        <SocketContext.Provider value={{ call, callAccepted, myVideo, userVideo, stream, setStream, setStream1, name, setName, callEnded, me, callUser, leaveCall, answerCall, screen, myScreenVideo, userScreenVideo, startScreenShare, stopScreenSharing, screenAccepted, screenEnded }} >
            {children}
        </SocketContext.Provider>
    );
};

export { ContextProvider, SocketContext }
