import React, { useState } from 'react'
import './Comments.css'

function DisplayComments({ commentBody, userCommented, id }) {
    const [Edit, setEdit] = useState(false)
    const [cmtBdy, setCmtBdy] = useState(commentBody)
    const handleEdit=(cmtId, cmtBody) => {
        setEdit(true)
        setCmtBdy(cmtBody)
    }
    function handleSubmit(event)
    {
        event.preventDefault()
        setEdit(false)
    }
    return (
        <>
            {
                Edit ? (
                    <>
                        <form action="" className='comments_sub_form_comments' onSubmit={handleSubmit}>
                            <input type="text" value={cmtBdy} placeholder='edit comment' className='comment_ibox' onChange={(e)=> setCmtBdy(e.target.value)} />
                            <input type="submit" value="change" className='comment_add_btn_comments' />
                        </form>
                    </>
                ) : (
                    <>
                        <p className='comment_body'>{cmtBdy}</p>
                    </>
                )
            }
            <p className="user_commented">- {userCommented} commented</p>
            <p className='EditDel_DisplayComment'>
                <i onClick={()=>handleEdit(id,cmtBody)}>Edit</i>
                <i>Delete</i>
            </p>
        </>
    )
}

export default DisplayComments
