import React, { useContext, useEffect, useRef } from 'react'
import { SocketContext } from '../../Context.js'
import './conf.css'
import ScreenVideo from '../../Component/Stream/ScreenVideo.jsx'

const ConferencePage = ({ mute, off }) => {
    const context = useContext(SocketContext)
    console.log(context)
    const { name, callAccepted, myVideo, userVideo, callEnded, stream, call } = context

    const handleMute = () => {
        return mute
    }

    const handleOff = () => {
        if (off) {
            return myVideo
        }
        else {
            return null
        }
    }

    return (
        <div>
            <ScreenVideo />
            <div className="gridContainer">
                {stream ? (
                    <>
                        <div className="paper">
                            <div className='Name'>{name === '' ? 'Name' : name}</div>
                            <video playsInline ref={myVideo} muted={handleMute()} autoPlay className='video' id='myvid' />
                        </div>
                    </>
                ) : (
                    <></>
                )
                }
                {
                    (callAccepted && !callEnded) ? (
                        <>
                            <div className="paper">
                                <div className='Name'>{name === '' ? 'Name' : name}</div>
                                <video playsInline ref={userVideo} muted={handleMute()} autoPlay className='video' />
                            </div>
                        </>
                    ) : (
                        <></>
                    )
                }
            </div>
        </div>
    )
}

export default ConferencePage
