import React, { useState } from 'react'
import './Comments.css'

function DisplayComments({ commentBody, userCommented }) {
    const [Edit, setEdit] = useState(false)
    const [commentText, setCommentText] = useState("")
    return (
        <>
            {
                Edit ? (
                    <>
                        <form action="" className='comments_sub_form_comments' onSubmit={handleOnSubmit}>
                            <input type="text" value={commentText} onChange={handleOnChange} placeholder='add comment' className='comment_ibox' />
                            <input type="submit" value="add" className='comment_add_btn_comments' />
                        </form>
                    </>
                ) : (
                    <>
                        <p className='comment_body'>{commentBody}</p>
                        <p className="user_commented">- {userCommented} commented</p>
                        <p className='EditDel_DisplayComment'>
                            <i>Edit</i>
                            <i>Delete</i>
                        </p>
                    </>
                )
            }
        </>
    )
}

export default DisplayComments
