import React, { useContext, useEffect, useRef } from 'react'
import { SocketContext } from '../../Context.js'
import './conf.css'


const ConferencePage = ({ screenShare }) => {
    const context = useContext(SocketContext)
    console.log(context)
    const { name, callAccepted, myVideo, userVideo, callEnded, stream, call, screenShareVideoMy, screenShareVideoUser, screen1 } = context
    const sref = useRef()
    useEffect(() => {
        if (sref.current && screen1) {
            console.log(screen1)
            sref.current.srcObject = screen1
        }
    }, [screen1])
    return (
        <div>
            <div className="gridContainer">
                {stream ? (
                    <>
                        {
                            screenShare ? (
                                <>
                                    <div className="screen">
                                        <video ref={screenShareVideoMy} autoPlay className='video1'></video>
                                    </div>
                                </>
                            ) : (
                                <></>
                            )
                        }
                        <div className="paper">
                            <div className='Name'>{name === '' ? 'Name' : name}</div>
                            <video playsInline muted ref={myVideo} autoPlay className='video' />
                        </div>
                    </>
                ) : (
                    <></>
                )
                }
                {
                    (callAccepted && !callEnded) ? (
                        <>
                            {
                                screenShare ? (
                                    <>
                                        <div className="screen">
                                            <video ref={sref} autoPlay className='video1'></video>
                                        </div>
                                    </>
                                ) : (
                                    <></>
                                )
                            }
                            <div className="paper">
                                <div className='Name'>{name === '' ? 'Name' : name}</div>
                                <video playsInline muted ref={userVideo} autoPlay className='video' />
                            </div>
                        </>
                    ) : (
                        <></>
                    )
                }
            </div>
        </div>
    )
}

export default ConferencePage
