import React from 'react'
import {WiCelsius} from 'react-icons/wi'
import './LocTemp.css'

const LocTemp = ({setlocbtn}) => {
    let Temp = document.getElementById('temp')
    let City = document.getElementById('city')
    let Desc = document.getElementById('Desc')
    const displayData = (weather) => {
        Temp.innerText = `${weather.main.temp}`
        Desc.innerText = `${weather.weather[0].description}`
        City.innerText = `${weather.name}`
        console.log(weather)
    }
    function getLocation() {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(ShowPos)
        }
        else {
            alert("GeoLocation Not Supported in your Device")
        }
    }
    function ShowPos(position) {
        fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${position.coords.latitude}&lon=${position.coords.longitude}&units=metric&appid=c11ef6bb5b19aebc55d38c97e77a3a55`)
            .then(res => res.json())
            .then(displayData)
            // .catch(err => alert("City Not Found"))
            .catch(err => console.log(err.message))
    }
    if(setlocbtn)
    {
        getLocation()
    }

    return (
        <div className="Loc_container" onClick={() => setlocbtn(false)}>
            <div className="Loc_container2">
                <div className="Temperature">
                    <p id='temp'>1</p> <WiCelsius style={{scale: "1",position: "relative", bottom: "5px",right: "14px"}}/>
                </div>
                <div className="City_Details" id='city'>1</div>
                <div className="weather_description" id='desc'>1</div>
            </div>
        </div>
    )
}

export default LocTemp
