import {MdPendingActions} from "react-icons/md"

const channelreducer = (states=[],action) =>
{
    switch (action.type) {
        case "UPDATE_DATA":
            return states.map(state => state.id === MdPendingActions.payload._id? action.payload : state )
        case "FETCH_CHANNELS":
            return action.paylaod
        default:
            return state
    }
}
export default channelreducer
