import React, { useContext, useEffect } from 'react';
import './singleUserCall.css'
import { ContextProvider, SocketContext } from '../../Context'
import Sidebar from './Sidebar';
import Notifications from './Notifications';

const SingleUserCall = () => {
  useEffect(()=>{
    const { name, callAccepted, myVideo, userVideo, callEnded, stream, call } = useContext(SocketContext);
  })
  return (
    <ContextProvider>
      <div>
        <div className="wrapper">
          <div className="appBar" position="static" color="inherit">
            <h2>Video Chat</h2>
          </div>

          <div className="gridContainer">
            {stream && (
              <div className="paper">
                <div>
                  <div>{name === '' ? 'Name' : name}</div>
                  <video playsInline ref={myVideo} autoPlay width={100} />
                </div>
              </div>
            )}
            {callAccepted && !callEnded && (
              <div className="paper">
                <div>
                  <div>{call.name || 'Name'}</div>
                  <video playsInline ref={userVideo} autoPlay className="video" />
                </div>
              </div>
            )}
          </div>
          <Sidebar>
            <Notifications />
          </Sidebar>
        </div>
      </div>
    </ContextProvider>
  )
}

export default SingleUserCall
