import * as api from "../Api";

export const setcurrentuser=(data)=>{
    return{
        type:"FETCH_CURRENT_USER",
        payload:data
    }
}

export const userpoint = (pointdata) => async(dispatch) => {
    try {
        const {id} = pointdata
        console.log(id)
        const {data} = await api.userpoints(id)
        dispatch({type:"POST_POINTS",payload:data})
    } catch (error) {
        console.log(error)
    }
}
