import React, { useState } from 'react'
import { BsThreeDots } from 'react-icons/bs'
import { MdPlaylistAddCheck } from 'react-icons/md'
import { RiPlayListAddFill } from 'react-icons/ri'

import './BtnsVideoPage.css'

function LikeWatchLaterSaveBtns() {
    const [saveVideo, setSaveVideo] = useState(false)
    function toggleSave()
    {
        if(saveVideo)
        {
            setSaveVideo(false)
        }
        else
        {
            setSaveVideo(true)
        }
    }
    return (
        <div>
            <div className="buttons_videoPage">
                <div className="btn_videoPage">
                    <BsThreeDots size={22} />
                </div>
                <div className="btn_videoPage" onClick={toggleSave}>
                    <div className="like_save_videoPage">
                        {
                            saveVideo ? (
                                <>
                                    <MdPlaylistAddCheck size={32} />
                                    <b>Saved</b>
                                </>
                            ) :
                            (
                                <>
                                    <RiPlayListAddFill size={22} />
                                    <b>Save</b>
                                </>
                            )
                        }
                    </div>
                </div>
            </div>
        </div>
    )
}

export default LikeWatchLaterSaveBtns
