import React, { useContext, useEffect, useRef } from 'react'
import { SocketContext } from '../../Context.js'
import './conf.css'


const ConferencePage = ({ screenShare }) => {
    const context = useContext(SocketContext)
    console.log(context)
    const { name, callAccepted, myVideo, userVideo, callEnded, stream, call, screenShareVideoMy, screenShareVideoUser } = context
    return (
        <div>
            <div className="screen">
                {
                    screenShare && screen && (
                        <>
                            <video className='video1' ref={screenShareVideoMy} playsInline autoPlay />
                            <video className='video1' ref={screenShareVideoUser} playsInline autoPlay />
                        </>
                    )
                }
            </div>
            <div className="gridContainer">
                {stream && (
                    <div className="paper">
                        <div className='Name'>{name === '' ? 'Name' : name}</div>
                        <video playsInline muted ref={myVideo} autoPlay className='video' />
                    </div>
                )}
                {callAccepted && !callEnded && (
                    <div className="paper">
                        <div className='Name'>{call.name || 'Name'}</div>
                        <video playsInline ref={userVideo} autoPlay className="video" />
                    </div>
                )}
            </div>
        </div>
    )
}

export default ConferencePage
