import React, { useContext } from 'react'
import './screenshare.css'
import { SocketContext } from '../../Context'

const ScreenVideo = () => {
    const { screen, myScreenVideo } = useContext(SocketContext)
    return (
        <>
            {
                screen && (
                    <div>
                        <video muted autoPlay controls ref={myScreenVideo} className='screenVid' />
                    </div>
                )
            }
        </>
    )
}

export default ScreenVideo
